INSERT INTO `kundenlogin13_emltpl` (`id`, `msg`, `betreff`) VALUES
	('lostlogin', '{grussf}\r\n\r\nIhre Zugangsdaten zu Ihrem Account lauten:\r\n\r\nBenutzer: {uf:5}\r\nPasswort: {uf:6}\r\n\r\nLogin unter: {url}\r\n\r\n\r\nMit freundlichen Grüßen\r\n\r\n{name}\r\n', 'Anforderung Ihrer Zugangsdaten'),
	('nltpl', '---------------------------------------------------------------\r\nNewsletter für {uf:2} {uf:3}\r\n---------------------------------------------------------------\r\n\r\n{grussf}\r\n\r\n\r\n\r\n\r\nMit freundlichen Grüßen\r\n\r\n{name}\r\n\r\n---------------------------------------------------------------\r\nKein Interesse mehr am Newsletter? Hier klicken zum abmelden:\r\n{solnk}\r\n---------------------------------------------------------------\r\n', ''),
	('register_fs0', '{grussf}\r\n\r\nvielen Dank für Ihre Registrierung bei {name}!\r\n\r\nSie können sich nun mit Ihren bei der Registrierung vergebenen Zugangsdaten in den Kundenbereich unter {url} einloggen.\r\n\r\n\r\nMit freundlichen Grüßen\r\n\r\n{name}\r\n', 'Vielen Dank für Ihre Registrierung, {uf:1} {uf:3}'),
	('register_fs1', '{grussf}\r\n\r\nvielen Dank für Ihre Registrierung bei {name}!\r\n\r\nKlicken Sie zur Freischaltung Ihres Accounts auf folgenden Link:\r\n\r\n{fslnk}\r\n\r\nSie können sich anschließend mit Ihren bei der Registrierung vergebenen Zugangsdaten in den Kundenbereich unter {url} einloggen.\r\n\r\n\r\nMit freundlichen Grüßen\r\n\r\n{name}\r\n', 'Vielen Dank für Ihre Registrierung, {uf:1} {uf:3}'),
	('register_fs2', '{grussf}\r\n\r\nvielen Dank für Ihre Registrierung bei {name}!\r\n\r\nIhr Account wird in Kürze durch einen Administrator verifiziert und anschließend freigeschaltet. Sie werden per E-Mail informiert, sobald Ihr Account verifiziert wurde.\r\n\r\nWurde Ihr Account erfolgreich verifiziert, können Sie sich mit Ihren bei der Registrierung vergebenen Zugangsdaten in den Kundenbereich unter {url} einloggen.\r\n\r\n\r\nMit freundlichen Grüßen\r\n\r\n{name}\r\n', 'Vielen Dank für Ihre Registrierung, {uf:1} {uf:3}'),
	('register_fs3', '{grussf}\r\n\r\nvielen Dank für Ihre Registrierung bei {name}!\r\n\r\nKlicken Sie zur Bestätigung Ihrer Registrierung auf folgenden Link:\r\n\r\n{fslnk}\r\n\r\nNach dem Klick auf den Bestätigungslink wird Ihr Account durch einen Administrator verifiziert und anschließend freigeschaltet. Sie werden per E-Mail informiert, sobald Ihr Account verifiziert wurde.\r\n\r\nWurde Ihr Account erfolgreich verifiziert, können Sie sich mit Ihren bei der Registrierung vergebenen Zugangsdaten in den Kundenbereich unter {url} einloggen.\r\n\r\n\r\nMit freundlichen Grüßen\r\n\r\n{name}\r\n', 'Vielen Dank für Ihre Registrierung, {uf:1} {uf:3}'),
	('fs_ok', '{grussf}\r\n\r\nIhr Account wurde durch einen Administrator überprüft und freigeschaltet.\r\n\r\nSie können sich nun mit Ihren bei der Registrierung eingegebenen Zugangsdaten unter {url} einloggen.\r\n\r\n\r\nMit freundlichen Grüßen\r\n\r\n{name}\r\n', 'Ihr Account wurde erfolgreich verifiziert'),
	('fs_del', '{grussf}\r\n\r\nIhr Account wurde durch einen Administrator überprüft und Ihre Registrierung wurde abgelehnt.\r\n\r\nIhr Account wurde aus unserem System gelöscht.\r\n\r\nBei Fragen stehen wir Ihnen gerne per E-Mail unter {email} zur Verfügung.\r\n\r\n\r\nMit freundlichen Grüßen\r\n\r\n{name}\r\n', 'Ihr Account wurde nicht verifiziert'),
	('change_email', '{grussf}\r\n\r\nSie haben Ihre E-Mail-Adresse geändert.\r\n\r\nKlicken Sie zur Freischaltung Ihres Accounts auf folgenden Link:\r\n\r\n{fslnk}\r\n\r\nErst nach einem Klick auf diesen Freischaltlink können Sie sich wieder in Ihren Account einloggen.\r\n\r\n\r\nMit freundlichen Grüßen\r\n\r\n{name}\r\n', 'Änderung Ihrer E-Mail-Adresse');
